import { useState } from "react";
function App() {

  const [count, setCount] = React.useState(0);  
  const isPrime = (num) => {
    if (num < 2) return false;
    for (let i = 2; i <= Math.sqrt(num); i++) {
      if (num % i === 0) return false;
    }
    return true;
  };
  return (
    <div>
      <div>
        <button data-testid="minusonebtn"  onClick={()=> setCount(count) } > +
        </button>
        <button data-testid="plusonebtn">-</button>
      <div>
        <button data-testid="resetbtn">reset</button>
      </div>
      </div>
      <span data-testid="counter"></span>
      <div>
        <span data-testid="odd-or-even"></span>
        <span data-testid="is-prime"></span>
      </div>
    </div>
  );

}

export default App;
