import { useState } from "react";
function App() {
  const [count, setCount] = useState(0);
  return (
    <div>
      <div>
        <button  onClick={()=> setCount(count-1)} data-testid="minusonebtn"> +</button>
        <button onClick={()=> setCount(count+1) }  data-testid="plusonebtn" >-</button>
      <div>
        <button  onClick={()=> setCount(0)} data-testid="resetbtn">reset</button>
      </div>
      </div>
      <span data-testid="counter"></span>
      <div>
        <span data-testid="odd-or-even" onClick={()=> setCount(count % 2 == 0 )? "even" : "odd"}>
        </span>
        <span   data-testid="is-prime" onClick={()=> setCount() ? "prime" : "prime-not"}></span>
      </div>
    </div>
  );

  };

export default App;
