import { useState } from "react";
function App() {
  // The Application should display if the number is `odd-or-even
  const [count, setCount] = useState(1);
  const isOddOrEven = count % 2 == 0 ? "even" : "odd";
  // The Application should display if the number is `is-prime`
  //  **NOTE:** 0 and 1 are not Prime numbers.
  const isPrime = (num) => {
    if (num <= 1) return false;
    for (let i = 2; i <= Math.sqrt(num); i++) {
      if (num % i === 0) return false;
    }

  }
  return (
    <div>
      <div>
        <button  onClick={()=> setCount(count-1)} data-testid="minusonebtn"> +</button>
        <button onClick={()=> setCount(count+1) }  data-testid="plusonebtn" >-</button>
      <div>
        <button  onClick={()=> setCount(0)} data-testid="resetbtn">reset</button>
      </div>
      </div>
      <span data-testid="counter"></span>
      <div>
        <span onClick={()=> setCount(count % 2 == 0 )} data-testid="odd-or-even"></span>
        <span onClick={()=> setCount(num % i === 0 )} data-testid="is-prime"></span>
      </div>
    </div>
  );

  };

export default App;
